import React, { useState } from "react";
import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./pages/Home";
import Booking from "./pages/Booking";
import Map from "./pages/Map";
import { Navigate, useLocation, useNavigate } from "react-router-dom";
import businfo from "./components/busInfo";

function App() {
  const isLoggedIn = true; // Replace with actual login state
  const [bustable, setBusinfo] = useState(businfo);
  const location = useLocation();
  const navigate = useNavigate();
  function SortByCity(bustable, city) {
    if (location.pathname !== "/map") {
      navigate("/map");
    }
    bustable = businfo.slice().filter((a) => a.city === city);
    setBusinfo(bustable);
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/home"
            element={
              <Home
                isLoggedIn={isLoggedIn}
                bustable={bustable}
                SortByCity={SortByCity}
              />
            }
          />
          <Route path="/booking" element={<Booking />} />
          <Route
            path="/map"
            element={
              <Map
                bustable={bustable}
                businfo={businfo}
                setBusinfo={setBusinfo}
                SortByCity={SortByCity}
              />
            }
          />
          <Route path="/" element={<Home isLoggedIn={isLoggedIn} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
