import React, { useState } from 'react';
import './SeatingLayout.css'; // Add necessary styling here

const Seat = ({ seatNumber, isSelected, isOccupied, onSeatClick }) => {
  const seatClass = `seat ${isSelected ? 'selected' : ''} ${isOccupied ? 'occupied' : ''}`;

  return (
    <div className={seatClass} onClick={() => !isOccupied && onSeatClick(seatNumber)}>
      {seatNumber}
    </div>
  );
};

const SeatingLayout = () => {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const occupiedSeats = [3, 4, 14, 29]; // Example occupied seats

  const handleSeatClick = (seatNumber) => {
    setSelectedSeats(prevSelectedSeats =>
      prevSelectedSeats.includes(seatNumber)
        ? prevSelectedSeats.filter(seat => seat !== seatNumber)
        : [...prevSelectedSeats, seatNumber]
    );
  };

  const renderSeats = () => {
    const seats = [];
    for (let i = 1; i <= 49; i++) {
      seats.push(
        <Seat
          key={i}
          seatNumber={i}
          isSelected={selectedSeats.includes(i)}
          isOccupied={occupiedSeats.includes(i)}
          onSeatClick={handleSeatClick}
        />
      );
    }
    return seats;
  };

  return (
    <div className="seating-layout">
      <div className="door">DOOR</div>
      <div className="seats">
        {renderSeats()}
      </div>
      <div className="rear">REAR</div>
    </div>
  );
};

export default SeatingLayout;
