import React from "react";
import Button from "react-bootstrap/Button";
import Bussearch from "../components/Bussearch";
import Footer from "../components/Footer";
import "../components/Header.css";
import Header from "../components/Header";
import "./Map.css";
import "./Home.css";
//import businfo from "../components/busInfo";

export default function Map() {
  return (
    <div className="home-container">
      <div className="head">
        <Header />
        <br />
        <br />
        <br />

        <div className="content">
          <br />
          <br />
          <div className="search-bar">
            <br />
            <Bussearch />
          </div>
        </div>
      </div>
      <Timetable />
      <div className="footer-wrapper">
        <Footer />
      </div>
    </div>
  );
}
function Timetable() {
  const color = [
    "primary",
    "secondary",
    "success",
    "danger",
    "warning",
    "info",
    "dark",
  ];
  const randnum = Math.floor(Math.random() * 7);
  //2console.log(randnum);
  let bustable = [
    {
      depature: "Colombo",
      fare: 1100,
      Name: "Mighty Travels",
      time: "10:00 AM",
      seats: 30,
    },
  ];
  return (
    <div className="list">
      <ul>
        {bustable.map((init) => (
          <Journey journey={init} color={color[randnum]} />
        ))}
      </ul>
    </div>
  );
}
function Journey({ journey, color }) {
  return (
    <li>
      <div>
        <Button variant={color}>{journey.Name}</Button>
        <p>Route number : {Math.floor(Math.random() * 300)}</p>
        <span>
          {journey.time} {journey.depature} {journey.fare} {journey.seats}
        </span>
      </div>
      <div></div>
    </li>
  );
}
