import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import ProgressBar from "react-bootstrap/ProgressBar";
import Bussearch from "../components/Bussearch";
import Footer from "../components/Footer";
import Header from "../components/Header";
import Sort from "./Sort";
import { ReactComponent as Film } from "../resources/film.svg";
import { ReactComponent as Location } from "../resources/geo-alt.svg";
import { ReactComponent as Temperature } from "../resources/thermometer-snow.svg";
import businfo from "../components/Timetable-components/busInfo";
import "./Map.css";
import "./Home.css";

export default function Map() {
  const [bustable, setBusinfo] = useState(businfo);
  const location = useLocation();
  const destination = location.state?.destination || "";
  const navigate = useNavigate();

  useEffect(() => {
    if (destination) {
      const filteredBustable = businfo.filter((bus) => bus.city === destination);
      setBusinfo(filteredBustable);
    }
  }, [destination]);

  const handleSortByName = () => {
    const sortedBustable = [...bustable].sort((a, b) => a.Name.localeCompare(b.Name));
    setBusinfo(sortedBustable);
  };

  const handleSortByAttribute = (attribute) => {
    const sortedBustable = [...bustable].sort((a, b) => a[attribute] - b[attribute]);
    setBusinfo(sortedBustable);
  };

  const handleSortByCity = (city) => {
    if (location.pathname !== "/map") {
      navigate("/map");
    }
    const filteredBustable = businfo.filter((bus) => bus.city === city);
    setBusinfo(filteredBustable);
  };

  return (
    <div className="home-container">
      <Header />
      <div className="content">
        <Bussearch bustable={bustable} SortByCity={handleSortByCity} />
        <Sort
          bustable={bustable}
          SortbyName={handleSortByName}
          SortbyAttribute={handleSortByAttribute}
        />
        <Timetable bustable={bustable} />
      </div>
      <Footer />
    </div>
  );
}

function Timetable({ bustable }) {
  const colorOptions = ["primary", "secondary", "success", "danger", "warning", "info", "dark"];

  return (
    <div className="list">
      <ul>
        {bustable.map((journey) => (
          <Journey key={journey.id} journey={journey} color={colorOptions[Math.floor(Math.random() * colorOptions.length)]} />
        ))}
      </ul>
    </div>
  );
}

function Journey({ journey, color }) {
  const { Name, city, arrival, departure, fare, seats } = journey;

  return (
    <li className="journey-item">
      <div className="BusDetail">
        <Button variant={color}>{Name}</Button>
        <p>Route number: {Math.floor(Math.random() * 300)}</p>
      </div>
      <div className="Schedule">
        <p>{`${city} to Matara`}</p>
        <ProgressBar variant="info" now={Math.floor(Math.random() * 100)} />
        <p>{`${formatTime(arrival)} - ${formatTime(departure)}`}</p>
        <small>Every journey minimally takes 2 hours</small>
      </div>
      <div className="Icons">
        <IconButtons />
        <p>View Cancellation Policy</p>
      </div>
      <div className="Ticket">
        <h5>LKR {fare}.00</h5>
        <p>Available Seats: {seats}</p>
        <Button variant={seats ? "success" : "danger"}>
          {seats ? "Available" : "Sold out"}
        </Button>
      </div>
    </li>
  );
}

function IconButtons() {
  return (
    <div className="Icon">
      <Button variant="outline-dark"><Film /></Button>
      <Button variant="outline-dark"><Location /></Button>
      <Button variant="outline-dark"><Temperature /></Button>
    </div>
  );
}

function formatTime(time) {
  const suffix = time < 12 ? "AM" : "PM";
  const formattedTime = time % 12 || 12; // Convert to 12-hour format
  return `${formattedTime}${suffix}`;
}
