import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Header from "../components/Header";
import Bussearch from "../components/Bussearch";
import Sort from "./Sort";
import Journey from "../components/Timetable-components/Journey";
import { viewAllBusses } from "../apiService/ApiService";

export default function Map() {
  const [bustable, setBustable] = useState([]);
  const [filteredBustable, setFilteredBustable] = useState([]);
  const location = useLocation();
  const destination = location.state?.destination || "";
  const navigate = useNavigate();

  useEffect(() => {
    const getBusDetails = async () => {
      try {
        const response = await viewAllBusses();
        setBustable(response.data);
        console.log(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    getBusDetails();
  }, []); // Only runs once on component mount

  useEffect(() => {
    if (destination) {
      const filtered = bustable.filter((bus) => bus.city === destination);
      setFilteredBustable(filtered);
    } else {
      setFilteredBustable(bustable); // Show all if no destination is selected
    }
  }, [destination, bustable]); // Only filters when destination or bustable changes

  function SortbyName() {
    const sortedBustable = bustable.slice().sort((a, b) => a.name.localeCompare(b.name));
    setFilteredBustable(sortedBustable);
  }

  function SortbyAttribute(attribute) {
    const sortedBustable = bustable.slice().sort((a, b) => a[attribute] - b[attribute]);
    setFilteredBustable(sortedBustable);
  }


  function SortByCity(city) {
    if (location.pathname !== "/map") {
      navigate("/map");
    }
    const filteredBustable = bustable.filter((bus) => bus.city === city);
    setFilteredBustable(filteredBustable);
  }
  return (
    <div className="home-container">
      <div className="head">
        <Header />
        <br />
        <br />
        <br />
        <div className="content">
          <br />
          <br />
          <div className="search-bar">
            <br />
            <Bussearch bustable={filteredBustable} />
          </div>
        </div>
      </div>
      <Sort bustable={filteredBustable} SortbyName={SortbyName} SortbyAttribute={SortbyAttribute} />
      <Timetable bustable={filteredBustable} />
    </div>
  );
}

function Timetable({ bustable }) {
  const color = [
    "primary",
    "secondary",
    "success",
    "danger",
    "warning",
    "info",
    "dark",
  ];

  return (
    <div className="list">
      <ul>
        {bustable.map((init) => (
          <Journey
            key={init.id} // Make sure each item has a unique key
            journey={init}
            color={color[Math.floor(Math.random() * 7)]}
          />
        ))}
      </ul>
    </div>
  );
}
