import React, { useState, useEffect } from "react";
import Bussearch from "../components/Bussearch";
import "../components/Header.css";
import Header from "../components/Header";
import "./Home.css";
import Sort from "./Sort";
import { useLocation, useNavigate } from "react-router-dom";
import Journey from "../components/Timetable-components/Journey";
import { viewAllBusses } from "../apiService/ApiService";

export default function Map() {
  const [bustable, setBusinfo] = useState([]);
  const location = useLocation();
  const destination = location.state?.destination || "";
  const navigate = useNavigate();

  // Fetch bus info from the API on component mount
  useEffect(() => {
    const getBusDetails = async () => {
      try {
        const response = await viewAllBusses();
        setBusinfo(response.data);
        console.log(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    getBusDetails();
  }, []);

  // Filter the bustable based on destination
  useEffect(() => {
    if (destination) {
      const filteredBustable = bustable.filter((bus) => bus.city === destination);
      setBusinfo(filteredBustable);
    }
  }, [destination, bustable]);

  function SortbyName() {
    const sortedBustable = bustable.slice().sort((a, b) => a.Name.localeCompare(b.Name));
    setBusinfo(sortedBustable);
  }

  function SortbyAttribute(attribute) {
    const sortedBustable = bustable.slice().sort((a, b) => a[attribute] - b[attribute]);
    setBusinfo(sortedBustable);
  }

  function SortByCity(city) {
    if (location.pathname !== "/map") {
      navigate("/map");
    }
    const filteredBustable = bustable.filter((bus) => bus.city === city);
    setBusinfo(filteredBustable);
  }

  return (
    <div className="home-container">
      <div className="head">
        <Header />
        <br />
        <br />
        <br />
        <div className="content">
          <br />
          <br />
          <div className="search-bar">
            <br />
            <Bussearch bustable={bustable} SortByCity={SortByCity} />
          </div>
        </div>
      </div>
      <Sort bustable={bustable} SortbyName={SortbyName} SortbyAttribute={SortbyAttribute} />
      <Timetable bustable={bustable} />
    </div>
  );
}

function Timetable({ bustable }) {
  const colors = ["primary", "secondary", "success", "danger", "warning", "info", "dark"];

  return (
    <div className="list">
      <ul>
        {bustable.map((init) => (
          <Journey key={init.Name} journey={init} color={colors[Math.floor(Math.random() * colors.length)]} />
        ))}
      </ul>
    </div>
  );
}
